import axios from "axios";
import { useEffect, useState } from "react";
import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { useNavigate } from "react-router";

export const Payment = () =>{
  const navigate=useNavigate()
    const[data,setdata]=useState([]);
    const[card,setcard]=useState(false);
    const[upi,setupi]=useState(false);
    const [Method,setMethod]=useState("")
    useEffect(()=>{
       getdata()
    },[])
    const getdata=()=>{
      axios.get('http://localhost:3001/cart').then((res)=>{
          setdata(res.data)
          console.log(res.data)
      })
    }
     let sum=0;
    data.map((e)=>{
        sum=sum+e.price
    })
    let GST=Math.round(sum*0.18);
    const handlecard=(event)=>{
        setMethod(event.target.value)
        if(event.target.value==="cdc"){
            setcard(true)
            setupi(false)
        }
        if(event.target.value==="upi"){
            setupi(true)
            setcard(false)
        }
        
    }
    return(
        <>
         <h1 className="heading">Payment Page</h1>
       <div className="paydiv">
        <div className="option">
        Select Payment Method<br></br>
        <Box sx={{ minWidth: 120 }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label"></InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={Method}
          label="Age"
          onChange={handlecard}
        >
          <MenuItem value={"cdc"} >Credit/Debit Card</MenuItem>
          <MenuItem value={"upi"}>UPI</MenuItem>
        </Select>
      </FormControl>
    </Box>
        <div style={{display:card?"block":"none",backgroundColor:"wheat",borderRadius:"10px"}}>
       
        <div>
        <span>Enter Name:</span><input type="text" className="cardinput" placeholder="Enter Cardholder Name"></input>
        </div>
        <div >
        <div className="imgcard">
        <div>
        <img style={{width:"100%"}} src="data:image/png;base64," alt="" />
        </div>
        <div>
        <img style={{width:"100%"}} src="https://w7.pngwing.com/pngs/92/785/png-transparent-mastercard-logo-mastercard-credit-card-payment-visa-nyse-ma-mastercard-logo-text-logo-sign.png" alt="" />
        </div>
        <div>
            <img style={{width:"100%"}} src="https://bfsi.eletsonline.com/wp-content/uploads/2019/09/RuPay-debit-card.png" alt="" />
        </div>
        <div>
            <img style={{width:"100%"}} src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fd/Maestro_logo.svg/2560px-Maestro_logo.svg.png" alt="" />
        </div>
        </div>
        </div>
        <div style={{marginTop:"10px"}}>
        <span>Card Number:</span><span><input className="cardinput" placeholder="Enter Card Number" type="text"></input></span>
        </div>
        <div style={{marginTop:"10px"}}>
        <span>CVV:</span><span><input style={{width:"10%"}} className="cardinput" placeholder="CVV" type="text"></input></span><span style={{marginLeft:"10%"}}>Expiry Date:</span><span><input style={{width:"8%"}} className="cardinput" placeholder="--/--" type="text"></input></span>
        </div>
        </div>
        <div style={{display:upi?"block":"none"}}>
        <div>
        <span>Enter Name:</span><input type="text" className="cardinput" placeholder="Enter full name"></input>
        </div>
        <div >
        <div className="imgcard">
        <div>
        <img style={{width:"100%"}} src="https://images.assettype.com/freepressjournal%2F2020-06%2F85479dab-ba32-4f5c-af57-df77c336d2d7%2Fgpay.jpg?rect=0%2C77%2C400%2C225" alt="" />
        </div>
        <div>
        <img style={{width:"100%"}} src="https://res.cloudinary.com/http-pixlok-com/image/upload/fl_attachment/Pixlok/Images/PNG/Paytm_Logo_thzrb7.png" alt="" />
        </div>
        <div>
            <img style={{width:"100%"}} src="https://image4.owler.com/logo/phonepe_owler_20190729_204807_original.png" alt="" />
        </div>
        <div>
            <img style={{width:"100%"}} src="https://trak.in/wp-content/uploads/2018/08/UPI-logo.jpg" alt="" />
        </div>
        </div>
        </div>
        <div style={{marginTop:"10px"}}>
        <span>UPI ID:</span><span><input className="cardinput" placeholder="Enter Valid UPI ID" type="text"></input></span>
        </div>
        <div style={{marginTop:"10px"}}>
        <span>Mobile Number</span><span><input style={{width:"40%"}} className="cardinput" placeholder="Enter Mobile Number" type="text"></input></span>
        </div>
        </div>
        <div>
        NOTICE:-
        <ul>
        <li>If payment gets failed the amout will redirect in your account within 3-4 working days.</li>
        <li>If server is down dont make any payment request.</li>
        <li>All payment methods are secure on this website.</li>
        <li>For customer support call 022-1234-5678</li>
        </ul>
        </div>
        <div></div>
        </div>
        <div className="ordersum">
          <h1>Order Summary</h1>
          <p>Details</p>
          {data.map((e)=><div key={e.id}>
          <p>{e.name}&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;₹{e.price}/-</p>
          </div>)}
          <hr></hr>
          <p>Subtotal:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;₹{sum}/-</p>
          <p>Tax:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;₹{GST}/-</p>
          <hr></hr>
          <h1>Total Bill</h1>
          <h1 style={{fontSize:"20px"}}>₹{sum+GST}/-</h1>
          <button onClick={()=>{navigate('/thankyou')}}>MAKE A PAYMENT</button>
           </div>
       </div>
        </>
    )
}